import React, {useState, useEffect} from "react";
import axios from "axios";
import './Products.css';

function Products() {
  const [productData, setProductData] = useState([]);
  const [loading, setLoading] = useState(true);

  const sendGetRequest = async () => {
    try {
      const resp = await axios("https://pet-lyfe-api.up.railway.app/");
      setProductData(resp.data);
    } catch (err) {
      // Handle Error Here
      console.error(err);
    }
  };
  useEffect(() => {
    sendGetRequest();
    setLoading(false);
  }, []);
  console.log(productData);

  if (!loading) {
    return (
      <div className="product-page">
        <div className="product-text">
          <h3 className="all-products">All Products</h3>
          <ul className="nav-products">
            <li></li>
          </ul>
        </div>

        <div className="product-cards"></div>
        {productData.map((product, index) => {
          return (
            <div key={index} className="product-card">
              <img
                className="product-image"
                src={product.img_thumb}
                alt={product.name}
              />
              <p className="product-name">{product.product_name}</p>
              <p className="price">Price: {product.retail_price}</p>
              <p className="rating">Rating: {product.rating}</p>
            </div>
          );
        })}
      </div>
    );
  }
}

export default Products;
